package com.lgalgol.leetcode.test392;

// 392. Is Subsequence
public class Test392 {
	public static void main(String[] args) {
		System.out.println(isSubsequence("abc", "ahbgdc"));
		System.out.println(isSubsequence("aabc", "ahabgdc"));
		System.out.println(isSubsequence("axc", "ahbgdc"));
		System.out.println(isSubsequence("leetcode", ""));
	}

	public static boolean isSubsequence(String s, String t) {
		int[] cIdx = new int[s.length()];
		
		int seq = -1;
		
		for(int i = 0; i < s.length(); i++) {
			cIdx[i] = t.indexOf(s.charAt(i), seq+1);

			if(cIdx[i] == -1 || cIdx[i] <= seq) {
				return false;
			}
			
			seq = cIdx[i];
		}
		
		return true;
		
	}
}
